import React,{useState} from "react";
import "./Comments.css"

const Comments = (props)=>{
     const messages = props.messages

    return (
        <div className="container_of_messages"  >
            {messages&&messages.map((val)=>(
                <div  className={`message ${val.uid}`} >
                    <div className="left">
                        <img src={val.photoURL} alt="" />
                    </div>
                    <div className="right">
                        <p>{val.content}</p>
                        <p>{val.uid}</p>
                        <p>{val.replies}</p>
                    </div>
                </div>
            ))}
        </div>
    )
}

export default Comments;
