import React,{useState,useEffect} from "react";
import "./style.css"
import Editor from "../../../Editor/Editor";
import useLocalStorage from "../../../../hooks/useLocalStorage"
const CSS_Challenges_left_Editor = ()=>{
    const [html, setHtml] = useLocalStorage("html", "");
  const [css, setCss] = useLocalStorage("css", "");
  const [srcDoc, setSrcDoc] = useState("");

  useEffect(() => {
    const timeout = setTimeout(() => {
      setSrcDoc(
        `
        <html>
          <body>${html}</body>
          <style>${css}</style>
        </html>
      `
        );
        }, 250);
    });

    return (
        <div className="CSS_Challenges_left_Editor_wrapper">
            <Editor
            language="xml"
            displayName="HTML"
            value={html}
            onChange={setHtml}
          />
          <Editor
            language="css"
            displayName="CSS"
            value={css}
            onChange={setCss}
          />
        </div>
    )
}

export default CSS_Challenges_left_Editor;
