import React,{useState,useEffect} from "react";
import { useAuthState } from "react-firebase-hooks/auth";
import firebase from "@firebase/app-compat";

import "./Comments.css"

const Comments = (props)=>{

  const [clicked,setClicked] = useState([]) ;
  const [user] = useAuthState(firebase.auth());

  let uid ;
  if(user){
      uid = user.uid ;
    }
    const messages = props.messages;

    useEffect(()=>{
        let arr = [] ;
        if(messages)
            for(let i=0 ; i< props.messages.length ; i++){
                arr[i] = false;
            }
        setClicked(arr);

    },[messages])

    const handleDelete = (i)=>{
        let arr = [...clicked] ;

        if(arr[i]==true){
            arr[i] = false
        }else
        arr = arr.map((val,index)=> i==index?!arr[index]:arr[index]) ;
        setClicked(arr) ;
    }




    return (
        <div className="container_of_messages"  >
            {messages&&messages.map((val,i)=>(
                <div  className={`message ${val.uid===uid?`sent`:`recieved`}`} onClick={()=>handleDelete(i)} >
                    <div className="left">
                        <img src={val.photoURL} alt="" />
                    </div>
                    <div className="right">
                        <p>{val.content}</p>
                        <p>{val.uid}</p>
                        <p>{val.replies}</p>
                    </div>
                    {clicked[i]&&uid===val.uid &&<div className="delete">
                            <button  >Delete Comment</button>
                        </div>}
                </div>
            ))}
        </div>
    )
}

export default Comments;
