import React , {useState} from "react";
import { useHistory } from "react-router";
import "./SignUpPage.css"

const SignUpPage = (props)=>{

    const [email,setEmail] = useState("");
    const [password,setPassword] = useState("");
    const [password1,setPassword1] = useState("");

    const history = useHistory()
    const [onLogin,setOnLogin] = useState(()=>{
        if(history.location.pathname[1]=='s'){
            return false;
        }else 
            return true;
    });


    const onHandleLoginForm = ()=>{
        setOnLogin(true)
    }
    const onHandleSignUpForm = ()=>{
        setOnLogin(false)
    }

    const googleLogin =  ()=>{
        props.location.data.googleLogin();
        history.push("/")
    }

    const postData = async (e)=>{
        e.preventDefault();
        console.log(email + password)
        if(password1 ){
            if(password!=password1 && password1!=""){
                alert("Enter similar passwords.")
            }
        }else{

            const res = await fetch("https://otp-login-22125-default-rtdb.firebaseio.com/youtubereactform.json.json"
                , {
                    method: "POST",
                    headers:{
                        "Content-Type":"application/json"
                    },
                    body: JSON.stringify({
                        email,
                        password
                    })
                }
            ) ;
        }
    }


    return (
        <div className="container">
        <div className="svgs">
            <img src="" id = "devSvg" />
        </div>
        <div className="loginBox">
            <div className="motto">
                One stop, full stack. Get started now!
            </div>
            <div className="content">
                <div className="googleSignin" onClick={googleLogin}  >
                    <img src="googleicon.png" />
                    <div>Continue with Google</div>
                </div>
                <div className="or">
                    OR
                </div>
                <div className="options">
                    <div id="login" className={onLogin?"active":""} onClick={onHandleLoginForm}>
                        Log in
                    </div>
                    <div id="signup" className={!onLogin?"active":""} onClick={onHandleSignUpForm}>
                        Sign up
                    </div>
                </div>

                <div className="entries">
                {onLogin?
                    (<form className="logIn" onSubmit={postData} >
                        <input type="text" placeholder="Username" id="user" autocomplete="off"  value={email} 
                            onChange={e=>setEmail(e.target.value)} required
                        />
                        <input type="password" placeholder="Password" id="pass" value={password}
                            onChange={e=>setPassword(e.target.value)} required
                        />
                        <div type="submit" className="continue" onClick={postData} >Continue</div>
                    </form>):

                    (<div className="signUp">
                        <input type="text" placeholder="Username" id="userNew" autocomplete="off" required 
                            value={email} onChange={e=>setEmail(e.target.value)}
                        />
                        <input type="password" placeholder="Password" id="passNew"  required 
                            value={password}  onChange={e=>setPassword(e.target.value)}
                        />
                        <input type="password" placeholder="Confirm Password" id="passCon" required 
                            value={password1}  onChange={e=>setPassword1(e.target.value)}
                        />
                        <button type="submit" className="continue" onClick={postData} >Continue</button>
                    </div>)
                }
                </div>
            </div>
        </div>
    </div>
    )
}

export default SignUpPage ;
