import React,{useState,useEffect} from "react";
import { useParams } from "react-router";
import "./CSS_Challenges_Page.css"
import svg4 from '../../../../svg4.svg'
import Editor from "../../../Editor/Editor";
import useLocalStorage from "../../../../hooks/useLocalStorage"

import CSS_Challenges_left_Editor from "../CSS_Challenges_Page_left_Editor.css/CSS_Challenges_Page_left_Editor";

const CSS_Challenges_Page = ()=>{

    const [html, setHtml] = useLocalStorage("html", "");
  const [css, setCss] = useLocalStorage("css", "");
  const [srcDoc, setSrcDoc] = useState("");


    const {id} = useParams()

    useEffect(() => {
        const timeout = setTimeout(() => {
          setSrcDoc(
            `
            <html>
              <body>${html}</body>
              <style>${css}</style>
            </html>
          `
            );
            }, 250);
        });

    return (
        <>
            {id>=1 && id<=21 ?  (
        <div className="CSS_Challenges_Page_wrapper">
            <div className="CSS_Challenges_Page_content">
                <div className="CSS_Challenges_Page_left">
                    {/* <CSS_Challenges_left_Editor /> */}
                    <Editor
            language="xml"
            displayName="HTML"
            value={html}
            onChange={setHtml}
          />
          <Editor
            language="css"
            displayName="CSS"
            value={css}
            onChange={setCss}
          />

                </div>
                <div className="CSS_Challenges_Page_right">
                    <div className="CSS_Challenges_Page_right_content">
                    <h2 data-text="ALTF4...">ALTF4...</h2>
                    </div>
                </div>
            </div>
            <h1>#DAY {id} <p>Use css and make the target.</p> </h1>
            <div className="CSS_Challenges_Page_bottom">
                <div className="CSS_Challenges_Page_bottom_content">
                    <h2>More Challenges Coming Soon...</h2>
                </div>
            </div>
        </div>
    ):<div className="alertmessage" >Page Not Found !</div>}
        </>
    )
}

export default CSS_Challenges_Page ;
