import React from "react";
import "./style.css"
const CSS_Challenges_left_Editor = ()=>{
    function compileHTML(field , e){
        document.getElementById('output').innerHTML = field.value;
      }

      function compileCSS(field , e){
        document.getElementById('styling').innerHTML = field.value;
      }
    return (
        <div className="CSS_Challenges_left_Editor_wrapper">

        <div class="parent" >
            <div class="container" id="cont" >
            <textarea placeholder="HTML goes here..." class="one"                   
                row="100"   column="100" id="one"  onkeyup={e=>compileHTML(e)} ></textarea>
            <textarea placeholder="CSS goes here..." class="two"  row="100"   column="100" id="two"
                onkeyup={(e)=>compileCSS(e)} ></textarea>
            </div>

            <div  id="output" class="outputScreen" ></div>
            </div>
            <style id="styling">
            </style>
        </div>
    )
}

export default CSS_Challenges_left_Editor;
