import React, { useState , useEffect } from "react";
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import EditorFinal from "./Components/Editor/EditorFinal";
import Projects from "./Components/Projects/Projects";
import Roadmap from "./Components/Roadmap/Roadmap";
import Home from './Components/Home'
import SignUpPage from "./Components/Nav/SignUpPage/SignUpPage";
import firebase from "./Firebase";
import { useAuthState } from "react-firebase-hooks/auth";
import 'firebase/compat/firestore';
import {useCollectionData} from "react-firebase-hooks/firestore" ;
import Comments from './Components/Comments/Comments'





function App() {

  const firestore = firebase.firestore();

  const [user] = useAuthState(firebase.auth());
  const messageRef = firestore.collection("comments")
  const query = messageRef.orderBy('content').limit(25);
  const [messages] = useCollectionData(query,{idField:'id'}) ;


  const sendMessage = async (e)=>{
    e.preventDefault();
    const {uid , photoURL} = firebase.auth().currentUser ;

    await messageRef.add({
      content:"hello , I am Hritik",
      createdAt : firebase.firestore.FieldValue.serverTimestamp(),
      uid,
      photoURL
    })
    console.log({
      content:"hello , I am Hritik",
      createdAt : firebase.firestore.FieldValue.serverTimestamp(),
      uid,
      photoURL
    })

  }
  return (
    <main>
      <BrowserRouter>
        <Home />
        <Switch>
            {/* <Route path="/" component={Home} exact /> */}
            <Route path="/EditorFinal" component={EditorFinal} />
            <Route path="/Projects" component={Projects} />
            <Route path="/Roadmap" component={Roadmap} />
            <Route path="/signup" component={SignUpPage} />
            <Route path="/login" component={SignUpPage} />
        </Switch>
        </BrowserRouter>
        <button onClick={(e)=>sendMessage(e)} > Send</button>
        <h2>Comments</h2>
    </main>
)
}

export default App;
