import React from "react";
import './style.css'

const Editor = () => {

  function compileHTML(field , e){
    document.getElementById('output').innerHTML = field.value;
  }

  function compileCSS(field , e){
    document.getElementById('styling').innerHTML = field.value;
  }

  function day(){

    document.getElementById("cont").style.color = "black";
    document.getElementById("cont").style.backgroundColor = "white";

  }

  function night(){

    document.getElementById("cont").style.color = "orange";
    document.getElementById("cont").style.backgroundColor = "black";


  }
  return (
    <div className="body" >
      <div className="top">
        <button onClick={night} class="night">
          Night
        </button>
        <button onClick={day} className="day">
          Day
        </button>
      </div>
      <div className="p">
        <div className="container" id="cont">
          <textarea
            placeholder="HTML goes here..."
            class="one"
            row="100"
            column="100"
            id="one"
            onkeyup={()=>compileHTML(this)}
          ></textarea>
          <textarea
            placeholder="CSS goes here..."
            class="two"
            row="100"
            column="100"
            id="two"
            onkeyup={()=>compileCSS(this)}
          ></textarea>
        </div>
        <div id="output"></div>
      </div>
      <style id="styling"></style>
    </div>
  );
};

export default Editor;
