import React , {useState} from "react";
import firebase from '../../../Firebase'
import { useHistory } from "react-router";
import "./SignUpPage.css"
import { useRef } from "react";
import Googleicon from '../../../googleicon.png'
import svg1 from '../../../svg1.svg'
import { useAuthState } from "react-firebase-hooks/auth";


const SignUpPage = (props)=>{

    const emailRef = useRef(null);
    const passwordRef = useRef(null);


    const [email,setEmail] = useState("");
    const [password,setPassword] = useState("");
    const [password1,setPassword1] = useState("");

    const history = useHistory()
    const [onLogin,setOnLogin] = useState(()=>{
        if(history.location.pathname[1]=='s'){
            return false;
        }else 
            return true;
    });


    const onHandleLoginForm = ()=>{
        setOnLogin(true)
    }
    const onHandleSignUpForm = ()=>{
        setOnLogin(false)
    }

    const googleLogin =  ()=>{
        if(props.location.data){
            props.location.data.googleLogin()
            history.push("/");

        }else
            console.log(props.location)
    }

    const signIn = async (e)=>{
        e.preventDefault();

    }


    return (
        <div className="container">
        <div className="svgs">
            <img src={svg1} id = "devSvg" />
        </div>
        <div className="loginBox">
            <div className="motto">
                One stop, full stack. Get started now!
            </div>
            <div className="content">
                <button className="googleSignin" onClick={googleLogin}  >
                    <img src={Googleicon}/>
                    <div>Continue with Google</div>
                </button>
                <div className="or">
                    OR
                </div>
                <div className="options">
                    <div id="login" className={onLogin?"active":""} onClick={onHandleLoginForm}>
                        Log in
                    </div>
                    <div id="signup" className={!onLogin?"active":""} onClick={onHandleSignUpForm}>
                        Sign up
                    </div>
                </div>

                <div className="entries">
                {onLogin?
                    (<form className="logIn" onSubmit={signIn} >
                        <input type="text" placeholder="Username" id="user" autocomplete="off"  value={email} 
                            onChange={e=>setEmail(e.target.value)} required ref={emailRef}
                        />
                        <input type="password" placeholder="Password" id="pass" value={password}
                            onChange={e=>setPassword(e.target.value)} required ref={passwordRef}
                        />
                        <div type="submit" className="continue continue_login" onClick={signIn} >Continue</div>
                    </form>):

                    (<div className="signUp">
                        <input type="text" placeholder="Username" id="userNew" autocomplete="off" required 
                            value={email} onChange={e=>setEmail(e.target.value)}
                        />
                        <input type="password" placeholder="Password" id="passNew"  required 
                            value={password}  onChange={e=>setPassword(e.target.value)}
                        />
                        <input type="password" placeholder="Confirm Password" id="passCon" required 
                            value={password1}  onChange={e=>setPassword1(e.target.value)}
                        />
                        <button type="submit" className="continue" onClick={""} >Continue</button>
                    </div>)
                }
                </div>
            </div>
        </div>
    </div>
    )
}

export default SignUpPage ;
