import React,{useState,useEffect} from "react";
import { useAuthState } from "react-firebase-hooks/auth";
import firebase from "@firebase/app-compat";
import {useCollectionData} from "react-firebase-hooks/firestore" ;

import "./Comments.css"

const Comments = (props)=>{

    const firestore = firebase.firestore();

    const [user] = useAuthState(firebase.auth());
    const messageRef = firestore.collection("comments")
    const query = messageRef.orderBy('content').limit(25);
    const [messages] = useCollectionData(query,{idField:'id'}) ;


    const sendMessage = async (e)=>{
      e.preventDefault();
      const {uid , photoURL} = firebase.auth().currentUser ;


      await messageRef.add({
        content:"hello , I am Hritik Sharma",
        createdAt : firebase.firestore.FieldValue.serverTimestamp(),
        uid,
        photoURL
      })
    }

    const [clicked,setClicked] = useState([]) ;

     let uid ;
     if(user)
        uid = user.uid ;

    const handleDelete = (i)=>{
        let arr = [...clicked] ;

        arr = arr.map((val,index)=> i==index?!arr[index]:false) ;
        setClicked(arr) ;
    }

    useEffect(()=>{
        if(messages)
            setClicked(Array(messages.length).fill(false))
    },[])



    return (
        <div className="wrapper">

        </div>
    )
}

export default Comments;
