import React,{useState} from "react";

const Comments = (props)=>{
     const messages = props.messages

    return (
        <div  >
            {messages&&messages.map((val)=>(
                <div  className="message" >
                    <p>{val.content}</p>
                    <p>{val.uid}</p>
                    <p>{val.replies}</p>
                </div>
            ))}
        </div>
    )
}

export default Comments;
