import React,{useState} from 'react'
import Editor from './Components/Editor/Editor';
function App() {
  const [html , setHtml] = useState("")
  const [css , setCss] = useState("")
  const [js , setJs] = useState("")
  return (
    <div  >
      <div className="pane top-pane" >
      <Editor 
        language="xml"
        displayName="HTML" 
        value={html} 
        onChange={setHtml}
      />
      <Editor 
        language="css"
        displayName="CSS" 
        value={css} 
        onChange={setCss}
      />
      <Editor 
        language="js"
        displayName="js" 
        value={js} 
        onChange={setJs}
      />
      </div>
      <div className="pane" >

      <iframe title="output"
      sandbox="allow-scripts"
      frameBorder="0"
      width="100%"
      height="100%"
      />
      </div>
    </div>
  );
};


export default App;
