import React, { useState, useEffect } from "react";
import { useParams } from "react-router";
import "./CSS_Challenges_Page.css";
import svg4 from "../../../../svg4.svg";
import Editor from "../../../Editor/Editor";
import useLocalStorage from "../../../../hooks/useLocalStorage";

import CSS_Challenges_left_Editor from "../CSS_Challenges_Page_left_Editor.css/CSS_Challenges_Page_left_Editor";

const CSS_Challenges_Page = () => {
  const [html, setHtml] = useLocalStorage("html", "");
  const [css, setCss] = useLocalStorage("css", "");
  const [srcDoc, setSrcDoc] = useState("");

  const { id } = useParams();

  useEffect(() => {
    const timeout = setTimeout(() => {
      setSrcDoc(
        `
            <html>
              <body>${html}</body>
              <style>${css}</style>
            </html>
          `
      );
    }, 250);
  });

  const [challenges,setChallenges] = useState([
      {
        "width" : "60%",
        "height" : "60%",
        "bg" : "orange"  ,
        "borderRadius" : 0
      },
      {
        "width" : "60%",
        "height" : "60%",
        "bg" : "orange"  ,
        "borderRadius" : "100%"
      },
  ]);

  return (
    <>
      {id >= 1 && id <= 21 ? (
        <div className="CSS_Challenges_Page_wrapper">
          <div className="CSS_Challenges_Page_content">
            <div className="CSS_Challenges_Page_left">
              <Editor
                language="xml"
                displayName="HTML"
                value={html}
                onChange={setHtml}
              />
              <Editor
                language="css"
                displayName="CSS"
                value={css}
                onChange={setCss}
              />
            </div>
            <div className="CSS_Challenges_Page_right">
              <div className="CSS_Challenges_Page_right_first">
                <iframe
                  srcDoc={srcDoc}
                  title="output"
                  sandbox="allow-scripts"
                  frameBorder="0"
                  width="100%"
                  height="100%"
                />
              </div>
              <div className="CSS_Challenges_Page_right_second">
                  <div className="CSS_Challenges_Page_right_second_content"
                    style={{width:challenges[1].width,height:challenges[1].height,background:challenges[1].bg , borderRadius:challenges[1].borderRadius}}
                  >

                  </div>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div className="alertmessage">Page Not Found !</div>
      )}
    </>
  );
};

export default CSS_Challenges_Page;
