import React, { useState } from "react";
import Carousel from "react-elastic-carousel";
import Card from "./Card";
import "./style.css";

const Explore_Courses_carousel = () => {

    const breakPoint = [
        {
            width : 500 , itemsToShwow : 1 
        },
        {
            width : 768 , itemsToShwow : 2 
        },
        {
            width : 1200 , itemsToShwow : 3 
        },
        {
            width : 1500 , itemsToShwow : 4 
        },
    ]

  const [items, setItems] = useState([
    { id: 1, title: "item #1" },
    { id: 2, title: "item #2" },
    { id: 3, title: "item #3" },
    { id: 4, title: "item #4" },
    { id: 5, title: "item #5" },
  ]);
  return (
    <div className="Explore_Courses_carousel_wrapper">
      <Carousel breakPoint >
        {items.map((item) => (
          <Card key={item.id} number={item.title} />
        ))}
      </Carousel>
    </div>
  );
};


export default Explore_Courses_carousel ;
