import React,{useState,useEffect} from "react";
import "./style.css"
import Editor from "../../../Editor/Editor";
import useLocalStorage from "../../../../hooks/useLocalStorage"
import svg4 from '../../../../svg4.svg'


const CSS_Challenges_left_Editor = ()=>{
    const [html, setHtml] = useLocalStorage("html", "");
  const [css, setCss] = useLocalStorage("css", "");
  const [srcDoc, setSrcDoc] = useState("");

  useEffect(() => {
    const timeout = setTimeout(() => {
      setSrcDoc(
        `
        <html>
          <body>${html}</body>
          <style>${css}</style>
        </html>
      `
        );
        }, 250);
    });

    return (
        <div className="CSS_Challenges_left_Editor_wrapper">
           <div className="CSS_Challenges_left_Editor_left">
           <Editor
            language="xml"
            displayName="HTML"
            value={html}
            onChange={setHtml}
          />
          <Editor
            language="css"
            displayName="CSS"
            value={css}
            onChange={setCss}
          />
           </div>
           <div className="CSS_Challenges_left_Editor_right">
           <iframe
            srcDoc={srcDoc}
            title="output"
            sandbox="allow-scripts"
            frameBorder="0"
            width="100%"
            height="100%"
          />
           </div>
           <div className="absolute_right_svg"></div>
           <img src={svg3} alt="" className="absolute_right_svg_im4" />
        </div>
    )
}

export default CSS_Challenges_left_Editor;
