import React,{useState} from 'react'
import Editor from './Components/Editor/Editor';
function App() {
  const [html , setHtml] = useState("")
  const [css , setCss] = useState("")
  const [js , setJs] = useState("")
  return (
    <div  >
      <Editor 
        language="xml"
        displayName="HTML" 
        value={html} 
        onChange={setHtml}
      />
      <Editor 
        language="css"
        displayName="CSS" 
        value={css} 
        onChange={setCss}
      />
      <Editor 
        language="js"
        displayName="js" 
        value={html} 
        onChange={setJs}
      />
      <div className="pane top-pane" ></div>
      <div className="pane" ></div>
      <iframe title="output"
      sandbox="allow-scripts"
      frameBorder="0"
      width="100%"
      height="100%"
      />
    </div>
  );
};


export default App;
